import React, { useState, useEffect } from 'react';

const AddEditPersonModal = ({ onClose, onSave, groups, personToEdit }) => {
  const [personName, setPersonName] = useState('');
  const [personDNI, setPersonDNI] = useState(''); // Nuevo estado para el DNI/ID
  const [selectedGroups, setSelectedGroups] = useState([]);

  useEffect(() => {
    if (personToEdit) {
      setPersonName(personToEdit.name);
      setPersonDNI(personToEdit.id); // Si edita, carga el ID existente
      setSelectedGroups(personToEdit.groupIds || []);
    } else {
      setPersonName('');
      setPersonDNI(''); // Vacío para nueva persona
      setSelectedGroups([]);
    }
  }, [personToEdit]);

  const handleGroupChange = (groupId) => {
    setSelectedGroups(prevSelected => {
      if (prevSelected.includes(groupId)) {
        return prevSelected.filter(id => id !== groupId);
      } else {
        return [...prevSelected, groupId];
      }
    });
  };

  const handleSave = () => {
    if (personName.trim() && personDNI.trim()) { // Validar que el DNI no esté vacío
      const dataToSave = {
        id: personToEdit ? personToEdit.id : personDNI.trim(), // Usar el DNI como ID para nuevas personas
        name: personName,
        groupIds: selectedGroups
      };
      onSave(dataToSave);
      onClose();
    } else {
      alert('Por favor, ingresa el nombre y el DNI de la persona.');
    }
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
      <div className="bg-white p-6 rounded-2xl shadow-2xl w-full max-w-md transform transition-all duration-300 scale-100 opacity-100">
        <h2 className="text-2xl font-bold text-gray-800 mb-6 text-center">
          {personToEdit ? 'Editar Persona' : 'Agregar Nueva Persona'}
        </h2>
        <input
          type="text"
          placeholder="Nombre de la Persona"
          value={personName}
          onChange={(e) => setPersonName(e.target.value)}
          className="w-full px-4 py-3 mb-4 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500 transition"
        />
        <input
          type="text"
          placeholder="DNI / ID de la Persona"
          value={personDNI}
          onChange={(e) => setPersonDNI(e.target.value)}
          className="w-full px-4 py-3 mb-4 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500 transition"
          disabled={!!personToEdit} // Deshabilitar si estamos editando (el ID no debe cambiar)
        />
        <div className="mb-6">
          <label className="block text-gray-700 text-lg font-medium mb-2">Selecciona Grupos:</label>
          <div className="grid grid-cols-2 gap-2">
            {groups.map((group) => (
              <label key={group.id} className="flex items-center space-x-2 bg-gray-50 p-3 rounded-lg cursor-pointer hover:bg-gray-100 transition-colors">
                <input
                  type="checkbox"
                  checked={selectedGroups.includes(group.id)}
                  onChange={() => handleGroupChange(group.id)}
                  className="form-checkbox h-5 w-5 text-blue-600 rounded"
                />
                <span className="text-gray-800">{group.name}</span>
              </label>
            ))}
          </div>
        </div>
        <div className="flex justify-end space-x-4">
          <button
            onClick={onClose}
            className="px-6 py-3 bg-gray-200 text-gray-800 rounded-xl hover:bg-gray-300 transition-colors font-semibold"
          >
            Cancelar
          </button>
          <button
            onClick={handleSave}
            className="px-6 py-3 bg-blue-600 text-white rounded-xl hover:bg-blue-700 transition-colors font-semibold"
          >
            {personToEdit ? 'Guardar Cambios' : 'Guardar Persona'}
          </button>
        </div>
      </div>
    </div>
  );
};

export default AddEditPersonModal;
