import React, { useState, useEffect } from 'react';
import LayoutHeader from './components/LayoutHeader';
import GroupView from './components/GroupView';
import PersonView from './components/PersonView';
import AddEditGroupModal from './components/AddEditGroupModal';
import AddEditPersonModal from './components/AddEditPersonModal';
import GroupDetailView from './components/GroupDetailView';
import MeetingsView from './components/MeetingsView';
import AddMeetingModal from './components/AddMeetingModal';
import MeetingDetailView from './components/MeetingDetailView';
import PersonDashboardView from './components/PersonDashboardView';
import RegisterAttendanceView from './components/RegisterAttendanceView';
import DeletedPeopleHistoryView from './components/DeletedPeopleHistoryView';
import AdminView from './components/AdminView';
import AdminLoginModal from './components/AdminLoginModal';
import HamburgerMenu from './components/HamburgerMenu';
import LeaderLoginModal from './components/LeaderLoginModal';
import { 
  leaderLogin, 
  leaderLogout, 
  isLeaderLoggedIn, 
  getLeaderData 
} from './utils/api';

const App = () => {
  const [currentPage, setCurrentPage] = useState('groups');
  const [selectedGroup, setSelectedGroup] = useState(null);
  const [selectedMeeting, setSelectedMeeting] = useState(null);
  const [selectedPerson, setSelectedPerson] = useState(null);
  const [showGroupModal, setShowGroupModal] = useState(false);
  const [groupToEdit, setGroupToEdit] = useState(null);
  const [showPersonModal, setShowPersonModal] = useState(false);
  const [personToEdit, setPersonToEdit] = useState(null);
  const [showAddMeetingModal, setShowAddMeetingModal] = useState(false);
  const [showAdminLoginModal, setShowAdminLoginModal] = useState(false);
  const [isAdminAuthenticated, setIsAdminAuthenticated] = useState(false);
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [showLeaderLoginModal, setShowLeaderLoginModal] = useState(false);
  const [currentLeader, setCurrentLeader] = useState(null);
  const [isLeaderAuthenticated, setIsLeaderAuthenticated] = useState(false);

  // Datos principales
  const [people, setPeople] = useState([]);
  const [meetings, setMeetings] = useState([]);
  const [groups, setGroups] = useState([]);
  const [deletedPeople, setDeletedPeople] = useState([]); // Se puede guardar en backend o mantener local

  // Verificar autenticación al cargar
  useEffect(() => {
    if (isLeaderLoggedIn()) {
      const leaderData = getLeaderData();
      setCurrentLeader(leaderData);
      setIsLeaderAuthenticated(true);
    }
  }, []);

  // Cargar datos al iniciar
  useEffect(() => {
    async function fetchData() {
      const [peopleData, meetingsData, groupsData] = await Promise.all([
        api.getPersons(),
        api.getMeetings(),
        api.getGroups(),
      ]);
      setPeople(peopleData);
      setMeetings(meetingsData);
      setGroups(groupsData);
    }
    fetchData();
  }, []);

  const handleSelectGroup = (group) => {
    setSelectedGroup(group);
    setCurrentPage('groupDetail');
  };

  const handleSelectPerson = (person) => {
    setSelectedPerson(person);
    setCurrentPage('personDashboard');
  };

  const handleSelectMeeting = (meeting) => {
    setSelectedMeeting(meeting);
    setCurrentPage('meetingDetail');
  };

  const handleBack = () => {
    if (currentPage === 'groupDetail') {
      setCurrentPage('groups');
      setSelectedGroup(null);
    } else if (currentPage === 'meetingDetail') {
      setCurrentPage('meetings');
      setSelectedMeeting(null);
    } else if (currentPage === 'personDashboard') {
      setCurrentPage('people');
      setSelectedPerson(null);
    } else if (currentPage === 'registerAttendance') {
      setCurrentPage('meetings');
    } else if (currentPage === 'deletedHistory') {
      setCurrentPage('people');
    } else if (currentPage === 'admin') {
      setCurrentPage('groups');
      setIsAdminAuthenticated(false);
    } else if (currentPage === 'adminLogin') {
      setCurrentPage('groups');
      setShowAdminLoginModal(false);
    }
  };

  // CRUD Personas
  const handleSavePerson = async (personData) => {
    if (personData._id) {
      const updated = await api.updatePerson(personData._id, personData);
      setPeople(people.map(p => p._id === updated._id ? updated : p));
    } else {
      const created = await api.createPerson(personData);
      setPeople([...people, created]);
    }
  };
  const handleDeletePerson = async (personId) => {
    await api.deletePerson(personId);
    setPeople(people.filter(p => p._id !== personId));
  };

  // CRUD Grupos
  const handleSaveGroup = async (groupData) => {
    if (groupData._id) {
      const updated = await api.updateGroup(groupData._id, groupData);
      setGroups(groups.map(g => g._id === updated._id ? updated : g));
    } else {
      const created = await api.createGroup(groupData);
      setGroups([...groups, created]);
    }
  };
  const handleDeleteGroup = async (groupId) => {
    await api.deleteGroup(groupId);
    setGroups(groups.filter(g => g._id !== groupId));
  };

  // CRUD Reuniones
  const handleAddMeeting = async (meetingData) => {
    const created = await api.createMeeting(meetingData);
    setMeetings([...meetings, created]);
  };
  const handleDeleteMeeting = async (meetingId) => {
    await api.deleteMeeting(meetingId);
    setMeetings(meetings.filter(m => m._id !== meetingId));
  };

  // CRUD Asistencias (puedes expandir según tu lógica)
  // ...

  const handleEditPerson = (person) => {
    setPersonToEdit(person);
    setShowPersonModal(true);
  };

  const handlePersonStatusChange = (personId, newStatus) => {
    setPeople(people.map(p =>
      p._id === personId ? { ...p, status: newStatus } : p
    ));
  };

  const handleMeetingAttendanceChange = (meetingId, personId, newStatus, excuse = '') => {
    setMeetings(meetings.map(meeting => {
      if (meeting._id === meetingId) {
        const updatedAttendance = meeting.attendance.map(att =>
          att.personId === personId ? { ...att, status: newStatus, excuse: newStatus === 'Excusa' ? excuse : '' } : att
        );
        if (!updatedAttendance.some(att => att.personId === personId)) {
          updatedAttendance.push({ personId: personId, status: newStatus, excuse: newStatus === 'Excusa' ? excuse : '' });
        }
        return { ...meeting, attendance: updatedAttendance };
      }
      return meeting;
    }));
  };

  const handleUpdateMeetingAttendance = (meetingId, newAttendanceRecords) => {
    setMeetings(meetings.map(meeting => {
      if (meeting._id === meetingId) {
        return { ...meeting, attendance: newAttendanceRecords };
      }
      return meeting;
    }));
  };

  const handleClearAllData = () => {
    if (window.confirm('¡Cuidado! ¿Estás seguro de que quieres borrar TODOS los datos (personas, reuniones, historial)? Esta acción es irreversible.')) {
      setPeople([]);
      setMeetings([]);
      setDeletedPeople([]);
      localStorage.removeItem('attendancePeople');
      localStorage.removeItem('attendanceMeetings');
      localStorage.removeItem('deletedPeopleHistory');
      alert('¡Todos los datos han sido borrados! La aplicación se reiniciará.');
      window.location.reload();
    }
  };

  const handleAdminLogin = (username, password) => {
    if (username === 'andresc' && password === 'andresc') {
      setIsAdminAuthenticated(true);
      setCurrentPage('admin');
      return true;
    }
    return false;
  };

  // Autenticación de líder
  const handleLeaderLogin = async (email, password) => {
    try {
      const response = await leaderLogin(email, password);
      setCurrentLeader(response.leader);
      setIsLeaderAuthenticated(true);
      setShowLeaderLoginModal(false);
    } catch (error) {
      throw error;
    }
  };

  const handleLeaderLogout = () => {
    leaderLogout();
    setCurrentLeader(null);
    setIsLeaderAuthenticated(false);
    setCurrentPage('groups');
  };

  const getTitle = () => {
    if (currentPage === 'groups') return 'Grupos';
    if (currentPage === 'people') return 'Personas';
    if (currentPage === 'meetings') return 'Reuniones';
    if (currentPage === 'groupDetail') return selectedGroup?.name || 'Detalle del Grupo';
    if (currentPage === 'personDashboard') return selectedPerson?.name || 'Dashboard de Persona';
    if (currentPage === 'meetingDetail') return selectedMeeting?.name || 'Detalle de Reunión';
    if (currentPage === 'registerAttendance') return 'Registrar Asistencia';
    if (currentPage === 'deletedHistory') return 'Historial de Eliminados';
    if (currentPage === 'admin') return 'Panel de Administración';
    return 'Asistencias';
  };

  const showBackButton = currentPage !== 'groups' && currentPage !== 'people' && currentPage !== 'meetings' && currentPage !== 'registerAttendance' && currentPage !== 'deletedHistory' && currentPage !== 'admin' && currentPage !== 'adminLogin';

  const renderContent = () => {
    if (!isLeaderAuthenticated) {
      return (
        <div className="flex-1 flex items-center justify-center bg-gray-50">
          <div className="text-center">
            <h1 className="text-3xl font-bold text-gray-800 mb-4">Sistema de Asistencias</h1>
            <p className="text-gray-600 mb-8">Inicia sesión como líder para continuar</p>
            <button
              onClick={() => setShowLeaderLoginModal(true)}
              className="bg-blue-600 text-white px-6 py-3 rounded-lg hover:bg-blue-700 transition-colors"
            >
              Iniciar Sesión
            </button>
          </div>
        </div>
      );
    }

    return (
      <>
        {currentPage === 'groups' && (
          <GroupView
            groups={groups}
            onSelectGroup={handleSelectGroup}
            onAddGroup={() => {}}
            onEditGroup={() => {}}
            onDeleteGroup={() => {}}
          />
        )}
        {currentPage === 'people' && (
          <PersonView
            people={people}
            onStatusChange={handlePersonStatusChange}
            onAddPerson={() => { setPersonToEdit(null); setShowPersonModal(true); }}
            onSelectPerson={handleSelectPerson}
            onEditPerson={handleEditPerson}
            onDeletePerson={handleDeletePerson}
          />
        )}
        {currentPage === 'meetings' && (
          <MeetingsView
            meetings={meetings}
            onSelectMeeting={handleSelectMeeting}
            onAddMeeting={() => setShowAddMeetingModal(true)}
            onDeleteMeeting={handleDeleteMeeting}
          />
        )}
        {currentPage === 'registerAttendance' && (
          <RegisterAttendanceView
            meetings={meetings}
            people={people}
            onUpdateMeetingAttendance={handleUpdateMeetingAttendance}
            onBack={() => setCurrentPage('meetings')}
            groups={groups}
          />
        )}
        {currentPage === 'deletedHistory' && (
          <DeletedPeopleHistoryView
            deletedPeople={deletedPeople}
            groups={groups}
          />
        )}
        {currentPage === 'admin' && isAdminAuthenticated && (
          <AdminView
            onClearAllData={handleClearAllData}
          />
        )}
        {currentPage === 'groupDetail' && selectedGroup && (
          <GroupDetailView
            group={selectedGroup}
            people={people}
            meetings={meetings}
            onStatusChange={handlePersonStatusChange}
            onAddPerson={() => { setPersonToEdit(null); setShowPersonModal(true); }}
            onSelectPerson={handleSelectPerson}
            onEditPerson={handleEditPerson}
            onDeletePerson={handleDeletePerson}
          />
        )}
        {currentPage === 'meetingDetail' && selectedMeeting && (
          <MeetingDetailView
            meeting={selectedMeeting}
            people={people}
            groups={groups}
            onStatusChange={handleMeetingAttendanceChange}
            onEditPerson={handleEditPerson}
          />
        )}
        {currentPage === 'personDashboard' && selectedPerson && (
          <PersonDashboardView
            person={selectedPerson}
            meetings={meetings}
            groups={groups}
          />
        )}
      </>
    );
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {isLeaderAuthenticated && (
        <LayoutHeader
          title={getTitle()}
          onBack={handleBack}
          showBack={currentPage !== 'groups' && currentPage !== 'people' && currentPage !== 'meetings'}
          onMenuToggle={() => setIsMenuOpen(!isMenuOpen)}
          currentLeader={currentLeader}
          onLeaderLogout={handleLeaderLogout}
        />
      )}

      <div className="flex">
        {isLeaderAuthenticated && (
          <HamburgerMenu
            isOpen={isMenuOpen}
            onClose={() => setIsMenuOpen(false)}
            onNavigate={setCurrentPage}
            currentPage={currentPage}
            isLeaderAuthenticated={isLeaderAuthenticated}
          />
        )}

        <main className="flex-1 p-6">
          {renderContent()}
        </main>
      </div>

      {/* Modals */}
      {showGroupModal && (
        <AddEditGroupModal
          onClose={() => setShowGroupModal(false)}
          onSave={handleSaveGroup}
          groupToEdit={groupToEdit}
        />
      )}

      {showPersonModal && (
        <AddEditPersonModal
          onClose={() => setShowPersonModal(false)}
          onSave={handleSavePerson}
          personToEdit={personToEdit}
          groups={groups}
        />
      )}

      {showAddMeetingModal && (
        <AddMeetingModal
          onClose={() => setShowAddMeetingModal(false)}
          onSave={handleAddMeeting}
          groups={groups}
        />
      )}

      {showAdminLoginModal && (
        <AdminLoginModal
          onClose={() => setShowAdminLoginModal(false)}
          onLogin={handleAdminLogin}
        />
      )}

      {showLeaderLoginModal && (
        <LeaderLoginModal
          onClose={() => setShowLeaderLoginModal(false)}
          onLogin={handleLeaderLogin}
        />
      )}
    </div>
  );
};

export default App;
